package com.osrapi.models.dwarfstar.barbarianprince;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Table;
import javax.validation.constraints.NotNull;

import com.fasterxml.jackson.annotation.JsonInclude;

/**
 * 
 * @author drau
 *
 */
@JsonInclude(JsonInclude.Include.NON_EMPTY)
@Entity
@Table(name = "event_type", schema = "dwarfstar_barbarian_prince")
public final class EventTypeEntity {
	/**
	 * the primary key - an autogenerated id (unique for each user in the db).
	 */
	@Id
	@Column(name = "event_type_id")
	@GeneratedValue(strategy = GenerationType.AUTO)
	private long id;
	/** the name of the event type. */
	@NotNull
	private String name;
	@NotNull
	private long value;
	/**
	 * @return the value
	 */
	public long getValue() {
		return value;
	}
	/**
	 * @param value the value to set
	 */
	public void setValue(long value) {
		this.value = value;
	}
	/** Creates a new instance of {@link EventTypeEntity}. */
	public EventTypeEntity() {
		super();
	}
	/**
	 * Creates a new instance of {@link EventTypeEntity}.
	 * @param n the name
	 */
	public EventTypeEntity(final String n) {
		name = n;
	}
	/**
	 * Gets the id.
	 * @return {@link long}
	 */
	public long getId() {
		return id;
	}
	/**
	 * Gets the name.
	 * @return {@link String}
	 */
	public String getName() {
		return name;
	}
	/**
	 * Sets the id.
	 * @param val the new value
	 */
	public void setId(final long val) {
		id = val;
	}
	/**
	 * Sets the name.
	 * @param val the new value
	 */
	public void setName(final String val) {
		name = val;
	}
}
